import './homepage.css';
import React, { useEffect, useState } from 'react';


const SubTitle = () => {};

const MiddleContatiner = () => {

    const [currIndex, setIndex] = useState(0);

    const l = [0,1,2];
    const imgs = ["./pics/1.jpg", "./pics/2.jpg", "./pics/3.jpg"];

    const DisplayImg = (index) => {
        return(
            <img src = {imgs[index]}> </img>
        )
    };

    const IndexButtons = () => {
        const handleClick = (index) => {
            setIndex(index);
        }

        const IndexingButton = (index) => {
            return(
            <button className='indexingButton' 
                    key = {index}
                    onClick={() => handleClick(index)}>
                {index}
            </button>)}

        useEffect(() => {
            console.log(currIndex);
        }, [currIndex]);

        return(
            l.map( i => IndexingButton(i))
            // imageBlock(imgs[currIndex]);
            );
    };


};


export default MiddleContatiner;
