import './homepage.css';
import React, { useEffect, useState } from 'react';


const SubTitle = () => {};

const MiddleContatiner = () => {

    const [currIndex, setIndex] = useState(0);

    const l = [0,1,2];
    const imgs = ['../public/1.jpg', '../public/1.jpg', '../public/1.jpg'];

    const DisplayImg = () => useEffect(()=>{
        <img src = {imgs[currIndex]}/>  
    },[currIndex]);

    const handleClick = (index) => {
        setIndex(index);
    }

    const IndexingButton = (index) => {
        return(
        <button className='indexingButton' 
                key = {index}
                onClick={() => handleClick(index)}>
            {index}
        </button>)}

    const IndexButtons = () => {
        return(
            l.map( i => IndexingButton(i))
            );
    };

    useEffect(() => {
        console.log(currIndex);
    }, [currIndex]);

    return(
        <>
        <IndexButtons/>
        <DisplayImg/>
        <img src = {require("../public/1.jpg")}/>
        </>

    )

};


export default MiddleContatiner;
