import './homepage.css';
import React, { useEffect, useState } from 'react';
import pic from './1.jpg'


const SubTitle = () => {};

const MiddleContatiner = () => {

    const [currIndex, setIndex] = useState(0);
    const l = [0,1,2];
    const imgs = ['./1.jpg', './2.jpg', './3.jpg'];

    const DisplayImg = () => {
        const imgPath = require('./'+ currIndex + '.jpg');
        return(
            <img src = {imgPath}/>
        )
    };

    const handleClick = (index) => {
        setIndex(index);
    }

    const IndexingButton = (index) => {
        return(
        <button className='indexingButton' 
                key = {index}
                onClick={() => handleClick(index)}>
            {index}
        </button>)}

    const IndexButtons = () => {
        return(
            l.map( i => IndexingButton(i))
            );
    };

    useEffect(() => {
        console.log(currIndex);
    }, [currIndex]);

    return(
        <>
        <IndexButtons/>
        DisplayImg
        <img src = {pic} alt = ""/>
        </>

    )

};


export default MiddleContatiner;
